document.addEventListener("DOMContentLoaded",()=>{const navToggle=document.querySelector(".nav-toggle");const sidebar=document.querySelector(".sidebar");const overlay=document.createElement("div");overlay.classList.add("overlay");document.body.appendChild(overlay);function closeSidebar(){sidebar.classList.remove("open");overlay.classList.remove("show");navToggle.setAttribute("aria-expanded","false")}
navToggle?.addEventListener("click",()=>{const isOpen=sidebar.classList.toggle("open");overlay.classList.toggle("show",isOpen);navToggle.setAttribute("aria-expanded",isOpen)});overlay.addEventListener("click",closeSidebar);document.addEventListener("keydown",(e)=>{if(e.key==="Escape")closeSidebar();});const dropdownToggle=document.querySelector(".dropdown-toggle");const dropdown=document.querySelector(".dropdown");dropdownToggle?.addEventListener("click",(e)=>{e.preventDefault();dropdown.classList.toggle("open")});const form=document.querySelector(".contact-form");if(form){form.addEventListener("submit",(e)=>{e.preventDefault();const name=form.querySelector("[name='name']").value.trim();const email=form.querySelector("[name='email']").value.trim();const message=form.querySelector("[name='message']").value.trim();const company=form.querySelector("[name='company']").value.trim();if(!name||!email||!message){alert("Please fill in all fields.");return}
const waNumber="+919967092210";const waText=encodeURIComponent(`Hello Adkom,\nMy name is ${name}\nFrom: ${company}\nEmail: ${email}\n${message}`);window.open(`https://wa.me/${waNumber}?text=${waText}`,"_blank");form.reset()})}
const sections=document.querySelectorAll("main > section");let currentIndex=0;function scrollToSection(index){if(index>=0&&index<sections.length){sections[index].scrollIntoView({behavior:"smooth"});currentIndex=index}}
document.addEventListener("keydown",(e)=>{if(e.key==="PageDown"||e.key==="ArrowDown"){e.preventDefault();scrollToSection(currentIndex+1)}else if(e.key==="PageUp"||e.key==="ArrowUp"){e.preventDefault();scrollToSection(currentIndex-1)}});const gallery=document.querySelector(".product-gallery");if(gallery){let startX=0;gallery.addEventListener("touchstart",(e)=>{startX=e.touches[0].clientX});gallery.addEventListener("touchend",(e)=>{let endX=e.changedTouches[0].clientX;if(endX-startX>50){gallery.scrollBy({left:-300,behavior:"smooth"})}else if(startX-endX>50){gallery.scrollBy({left:300,behavior:"smooth"})}});gallery.addEventListener("keydown",(e)=>{if(e.key==="ArrowLeft"){gallery.scrollBy({left:-300,behavior:"smooth"})}
if(e.key==="ArrowRight"){gallery.scrollBy({left:300,behavior:"smooth"})}})}});const style=document.createElement("style");style.textContent=`
  .overlay {
    position: fixed;
    top: 0; left: 0;
    width: 100%; height: 100%;
    background: rgba(0,0,0,0.4);
    opacity: 0; visibility: hidden;
    transition: opacity 0.3s;
    z-index: 998;
  }
  .overlay.show {
    opacity: 1;
    visibility: visible;
  }
`;document.head.appendChild(style);const header=document.querySelector("header");if(header.classList.contains('index')){window.addEventListener("scroll",()=>{if(window.scrollY>50){header.classList.add("scrolled")}else{header.classList.remove("scrolled")}})}else{header.classList.add('scrolled')}
const gallery=document.querySelector(".product-gallery");if(gallery){const slides=gallery.querySelectorAll(".gallery-slide");const prevBtn=gallery.querySelector(".gallery-prev");const nextBtn=gallery.querySelector(".gallery-next");let currentIndex=0;function showSlide(index){slides.forEach((slide,i)=>{slide.classList.toggle("active",i===index)})}
function nextSlide(){currentIndex=(currentIndex+1)%slides.length;showSlide(currentIndex)}
function prevSlide(){currentIndex=(currentIndex-1+slides.length)%slides.length;showSlide(currentIndex)}
prevBtn.addEventListener("click",prevSlide);nextBtn.addEventListener("click",nextSlide);gallery.addEventListener("keydown",(e)=>{if(e.key==="ArrowLeft")prevSlide();if(e.key==="ArrowRight")nextSlide();});setInterval(nextSlide,5000)}√è